import React, { Component } from 'react';
import CardList from '../Components/CardList';
import OneMovie from '../Components/OneMovie';
import Showcase from '../Components/Showcase';
import Navigation from '../Components/Navigation';
import apiKey from '../api.js';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      popular: [],
      top_rated: [],
      upcoming: [],      
      movieSelect: {},
      cast: [],
      crew:[],      
      route: 'home',
      searchText: ''      
    }    
  };

  componentDidMount() {
    const movieCategories = ['popular', 'top_rated', 'upcoming'];
    movieCategories.forEach((movieCategory, i) => {
      fetch(`https://api.themoviedb.org/3/movie/${movieCategory}?api_key=${apiKey}&language=en-US&page=1`)  
        .then(resp => resp.json())
        .then(data => this.setState({[movieCategory]: data.results}) )          
    })
    //scroll event     
    window.addEventListener('scroll', this.handleScroll, true);
  };

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleSearch = (e) => {
    this.setState({searchText: e.target.value});    
  };

  movieClick = (id) => {
    fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=${apiKey}`)
      .then(resp => resp.json())
      .then(movie => {
        if(movie) { this.setState({movieSelect: movie, route: 'movie'});      
        } else { console.log(movie) }               
      })

    fetch(`https://api.themoviedb.org/3/movie/${id}/credits?api_key=${apiKey}`)
      .then(resp => resp.json())
      .then(credit => this.setState({
        cast: credit.cast,
        crew: credit.crew
      }))          
  };

  logoClick = (route) => {
    this.setState({route: route})          
  }

  handleScroll = () => {
    let y = window.scrollY;
    //use plain js codes
    const navbar = document.querySelector('.navbar');    
    if(y > 60) {
      navbar.style.backgroundColor = "black";
    } else {
      navbar.style.backgroundColor = "transparent";
    }
  }

  render() {    
    let moviePick, searchResults = [];
    const { popular, top_rated, upcoming, movieSelect, cast, crew, route, searchText } = this.state;
    //data  validation for state properties
    if(!popular[0] || !top_rated[0] || !upcoming[0]) {
      moviePick = []  
    } else {
      moviePick = [popular[0], top_rated[3], upcoming[0]]  
    }

    if(searchText) {
      fetch(`https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${searchText}`)
        .then(resp => resp.json())
        .then(movies => {
          getSearch.push(movies.results);
          console.log(getSearch)})
    }
    console.log(searchResults);

    return (      
      <div>
        <Navigation           
          logoClick={this.logoClick} 
          handleScroll={this.handleScroll}
          handleSearch={this.handleSearch}
          getSearch={getSearch}
          movieClick={this.movieClick}
        />
        {route === 'home'?
          <div>            
            <Showcase 
              movies={moviePick}
              movieClick={this.movieClick}
            />
            <div className="movie-category">
              <h1 id="popular">Popular</h1>
              <CardList 
                movieClick={this.movieClick}
                movies={popular}          
              />
              <h1 id="top-rated">Top-rated</h1>
              <CardList 
                movieClick={this.movieClick}
                movies={top_rated}          
              />
              <h1 id="upcoming">Upcoming</h1>
              <CardList 
                movieClick={this.movieClick}
                movies={upcoming}          
              />  
            </div>                       
          </div>
          :<div>            
            <OneMovie 
              movieSelect={movieSelect} 
              cast={cast}
              crew={crew}
            />
          </div>
        }
      </div>
    )
  }  
}



export default App;




