import React, { Component } from 'react';
import CardList from '../Components/CardList';
import OneMovie from '../Components/OneMovie';
import apiKey from '../api.js';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: [],
      route: false,
      id: null,
      searchfield: ''    
    }    
  };

  componentDidMount() {
    fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=1`)
      .then(resp => resp.json())
      .then(data => this.setState({
        movies: data.results
      }))
  };

  handleSearch = (e) => {
    this.setState({
      searchfield: e.target.value
    });
  };

  movieClick = (id) => {
    this.setState({
      route: !this.state.route,
      id: id
    });    
  };

  render() {
    const { movies, route, id } = this.state        
    return (
      /*<navigation /> inside is a searchfield*/
      <div>
        {!route?
          <div>
            {/*<showcase /> */}
            <h1>Lastest</h1>        
            <h1>Top-rated</h1>
            <h1>Upcoming</h1>
            <h1>Popular</h1>
            <CardList 
              movieClick={this.movieClick}
              movies={movies}          
            />
          </div>
          :<div>
            <OneMovie id={id} />
          </div>
        }
      </div>
    )
  }  
}



export default App;




