import React from 'react';
import apiKey from '../api.js';

function OneMovie( {id} ) {
  fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=${apiKey}`)
    .then(resp => resp.json())
    .then(data => {
    	const { 
    		backdrop_path, 
    		title, 
    		tagline, 
    		overview, 
    		release_date,
    		runtime,

    	} = data;
    })

  return(
  	<div>
  		<img src={`http://image.tmdb.org/t/p/original/${backdrop_path}`} />		


  	</div>

  )
}



export default OneMovie;
