          <FontAwesomeIcon 
            icon={faSearch} 
            size="lg" 
            onClick={iconClick} 
          />
          <input 
            type="text" 
            placeholder="Title, people, genre" 
            onChange={handleSearch}
          />
        </li>
      </ul>
