import React, { Component } from 'react';
import CardList from '../Components/CardList';
import SearchBox from '../Components/SearchBox';
import './App.css';


const apiKey = '17c3fa534657a7495834bc8fb45a9c05';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //import the data, put it in the initial state
      movies: [],
      searchfield: ''
    }
    this.handleSearch = this.handleSearch.bind(this);
  };

  componentDidMount() {
    fetch(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=1`)
      .then(resp => resp.json())
      .then(data => this.setState({
        movies: data.results
      }))
  }

  handleSearch(e) {
    //directly take the event from the child 
    this.setState({
      searchfield: e.target.value
    });
  }

  render() {
    const { movies, searchfield } = this.state
    console.log(movies);
    const filteredRobots = movies.filter(movie => {
        return movie.title.toLowerCase().includes(searchfield)
        })
    return (
      //use turnary in the return()
      !robots.length? 
        <div>Loading</div>:
        <div>
          <h1>RoboFriend</h1>
          <SearchBox 
            searchfield={searchfield} 
            onSearch={this.handleSearch}
          />
          <CardList 
            robots={filteredRobots}
            searchfield={searchfield}  
          />
        </div>
    )
  }  
}



export default App;
