import React from 'react';


function OneMovie( {movieSelect, cast, crew} )  {  
  const { backdrop_path, title, vote_average, tagline, overview, release_date, budget, poster_path, genres, runtime, revenue } = movieSelect; 
  console.log(crew);
  const director = crew.filter(crew => {
  	if(crew.job === 'Director') {
  		return crew.name;
  	}
  })
  console.log(director);
  const genreList = genres.map(genre => {
  	//last comma
  	return (
  		<span key={genre.id}>
  			{`${genre.name}, `}
  		</span>
  	)
  }) 
  return(
    <div className="one-movie">
	  <div className="showcase">		  	
	    <img src={`http://image.tmdb.org/t/p/w1280/${backdrop_path}`} alt={title} />
	    <div className="info">
	  	  <h1>{title}</h1>
	  	    <p className="rating">Rating: <span>{vote_average}</span></p>
	  	    <p>{tagline}</p>
	  	  </div>
	  </div>
	  <div className="up-section">  	    
  	    <div className="details">
  	  	  <h1>{title}</h1>
  	  	  <p>Director: {}</p>
  	  	  <p>Release date: {release_date}</p>
  	  	  <p>Runtime: {runtime}minutes</p>
  	  	  <p>Revenue: {new Intl.NumberFormat('en-US', {style: 'currency', currency: 'USD'}).format(revenue)}</p>
  	  	  <p>Budget: {new Intl.NumberFormat('en-US', {style: 'currency', currency: 'USD'}).format(budget)}</p>
  	  	  <p>Genres: {genreList}</p>
  	  	  <p>Plot:<br/>
  	  	  	{overview}
  	  	  </p>
  	    </div>
  	    <img src={`http://image.tmdb.org/t/p/w185/${poster_path}`} alt={title} />  	    
 	  </div>
	</div>
  )	  
}



export default OneMovie;
