import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

function Navigation( {logoClick} )  {
  //input field onchange
  //pass the text to state
  //search from api

  //icon onclick
  //expand the searchfield
  //show border


  const searchBox = () => {
    // const searchIcon = document.querySelector('.fa-search');
    const searchField = document.querySelector('.search');
    const inputText = document.querySelector('input::placeholder');
    searchField.style.borderColor = '#fff';
    searchField.style.width = '300px';
    inputText.style.color = '#fff';


  }  

  return(
	  <nav className="navbar">
      <ul>
        <li className="logo" onClick={()=>logoClick('home')}><a href="#home">M</a></li>
        <li><a href="#popular">Popular</a></li>
        <li><a href="#top-rated">Top Rated</a></li>
        <li><a href="#upcoming">Upcoming</a></li>
        <li className="search" onclick={searchBox}>
          <FontAwesomeIcon icon={faSearch} size="lg" />
          <input type="text" placeholder="Title, people, genre"/>
        </li>
      </ul>

	  </nav>



  	)	  
}



export default Navigation;
