import React from 'react';
import Card from './Card';

function CardList( {movies, movieClick} ) { 
  const movieLists = movies.map((list, i) => { 
    return (
      <div className="card-wrapper">
        <div className="arrow arrow-left" onClick={() => handleClick(`${i}a`)}>{`<`}</div> 
        <div className="card-list">
          {list.map(movie => { return (
              <Card key={movie.id} movieClick={movieClick} movie={movie} /> )})}          
        </div>
        <div className="arrow arrow-right" onClick={() => handleClick(`${i}b`)}>{`>`}</div> 
      </div> 
    )
  })
  //refractor these codes?
  let ml0 = 0, ml1 = 0, ml2 = 0;  

  const slider = (n) => {
    //0, a 
    const { list, arrow } = n.split('');
    cardLists[list].style.marginLeft = -90;
  } 


  const handleClick = (n) => {    
    const cardLists = document.querySelectorAll('.card-list');







    if(n === '0a' || n === '0b') {
      if(n === '0b' && ml0 > -270) { ml0 = ml0 - 90 } 
        else if( n === '0a' && ml0 < 0 ) { ml0 = ml0 + 90}
      cardLists[0].style.marginLeft = `${ml0}%`;
      }  else if(n === '1a' || n === '1b') {
      if(n === '1b' && ml1 > -270) { ml1 = ml1 - 90 } 
        else if( n === '1a' && ml1 < 0 ) { ml1 = ml1 + 90}
      cardLists[1].style.marginLeft = `${ml1}%`;
      } else if ( n === '2a' || n === '2b') {
      if(n === '2b' && ml2 > -270) { ml2 = ml2 - 90 } 
        else if( n === '2a' && ml2 < 0 ) { ml2 = ml2 + 90}
      cardLists[2].style.marginLeft = `${ml2}%`;
      }    
  }  

  return (
    <div>
      <h1 id="popular">Popular</h1>    
        {movieLists[0]}
      <h1 id="top-rated">Top-rated</h1>
        {movieLists[1]}
      <h1 id="upcoming">Upcoming</h1>
        {movieLists[2]}
    </div>

  );
}

export default CardList;
